                HTML_cat_item_new.querySelector('a').href = `${hasPrefixKK ? '/kk' : ''}`+'/prodavcy/'+store_block[i].slug;
                HTML_cat_item_new.querySelector('a').innerHTML = "<div class=\"store_search_result\">" +
